INSERT INTO public.question (id, text, answer) VALUES (2296, 'Металлический, пробковый, пластмассовый и т.п. головной убор в виде шлема (у военных, пожарных, строителей и т.п.), защищающий голову от повреждений.', 'каска');
INSERT INTO public.question (id, text, answer) VALUES (2304, 'Отрицательно заряженный электрод.', 'катод');
