INSERT INTO public.question (id, text, answer) VALUES (2329, 'Откидная крышка различных механизмов.', 'капот');
INSERT INTO public.question (id, text, answer) VALUES (2336, 'Пучок щетины, волоса или синтетических волокон, насаженный на рукоятку, для нанесения краски, клея и т.п.', 'кисть');
