INSERT INTO public.question (id, text, answer) VALUES (2032, 'Традиционное итальянское блюдо в виде тонкой лепешки из теста с запеченными на ней под соусом кусочками мяса, сыра, овощей и т.п.', 'пицца');
INSERT INTO public.question (id, text, answer) VALUES (2040, 'Сплошной слой, массив однородного по составу, расположенный на значительном протяжении.', 'пласт');
