INSERT INTO public.question (id, text, answer) VALUES (2089, 'Сидячая форма особи кишечнополостных животных.', 'полип');
INSERT INTO public.question (id, text, answer) VALUES (2097, 'Одна из двух загнутых спереди и скользящих по снегу пластин, на которых закреплены сани.', 'полоз');
INSERT INTO public.question (id, text, answer) VALUES (2105, 'Этническая и социальная общность людей, связанных родовыми отношениями, территорией, культурой, языком и самоназванием.', 'племя');
INSERT INTO public.question (id, text, answer) VALUES (2115, 'Луковичное огородное растение с широкими плоскими листьями, употребляемое в пищу как приправа.', 'порей');
