INSERT INTO public.question (id, text, answer) VALUES (2211, 'Артиллерийское орудие с длинным стволом, предназначенное для прицельной стрельбы по наземным, морским и воздушным целям, а также по целям, расположенным на больших расстояниях.', 'пушка');
INSERT INTO public.question (id, text, answer) VALUES (2219, 'Пышная круглая булочка.', 'пышка');
INSERT INTO public.question (id, text, answer) VALUES (2227, 'Отверстие для вливания чугуна в форму при отливке; литник.', 'путец');
INSERT INTO public.question (id, text, answer) VALUES (2235, 'Титул главы государства у древних тюркских народов.', 'каган');
INSERT INTO public.question (id, text, answer) VALUES (2243, 'След, оставленный на поверхности пачкающим.', 'пятно');
