INSERT INTO public.question (id, text, answer) VALUES (2314, 'Доля в производстве, сбыте или экспорте продукции, устанавливаемая в рамках соглашения для каждого из участников.', 'квота');
INSERT INTO public.question (id, text, answer) VALUES (2321, 'Чесальная или ворсовальная машина; кардная машина.', 'карда');
INSERT INTO public.question (id, text, answer) VALUES (2328, 'Высокий жесткий головной убор - цилиндрической или конусообразной формы - с козырьком и подбородочным ремнем (в некоторых европейских армиях XVIII-XIX вв., в том числе и в русской кавалерии до 1917 г.).', 'кивер');
