INSERT INTO public.question (id, text, answer) VALUES (2438, 'Верхний затвердевший слой.', 'корка');
INSERT INTO public.question (id, text, answer) VALUES (2447, 'Морское млекопитающее семейства ушастых тюленей с густым и мягким подшерстком, обитающее в северной части Тихого океана.', 'котик');
