INSERT INTO public.question (id, text, answer) VALUES (2475, 'Клин земли.', 'косяк');
INSERT INTO public.question (id, text, answer) VALUES (2486, 'Часть автомашины, вагона и т.п., служащая для размещения людей или грузов.', 'кузов');
