INSERT INTO public.question (id, text, answer) VALUES (2503, 'Детская игрушка в виде фигурки человека.', 'кукла');
INSERT INTO public.question (id, text, answer) VALUES (2511, 'Лицо, принадлежавшее к купеческому сословию (в Российском государстве до 1917 г.).', 'купец');
