INSERT INTO public.question (id, text, answer) VALUES (2628, 'Вьющаяся или завитая прядь волос.', 'локон');
INSERT INTO public.question (id, text, answer) VALUES (2637, 'Балтийские племена скотоводов и земледельцев, пришедшие в бассейны рек Неман и Даугава (предшественники современных литовцев).', 'литва');
INSERT INTO public.question (id, text, answer) VALUES (2643, 'Промысловая рыба семейства кефалей.', 'лобан');
INSERT INTO public.question (id, text, answer) VALUES (2362, 'Печатное издание (в старину - также рукопись) в виде сброшюрованных, переплетенных вместе листов с текстом.', 'книга');
