INSERT INTO public.question (id, text, answer) VALUES (2377, 'Гимнастический снаряд в виде обитого кожей короткого бруса на четырех ножках.', 'козел');
INSERT INTO public.question (id, text, answer) VALUES (2388, 'Настил из досок, укрепленных на стойках из деревянных брусьев, сбитых крест-накрест, используемый в качестве подставки, для работы на высоте и т.п.', 'козлы');
INSERT INTO public.question (id, text, answer) VALUES (2393, 'Цилиндрический мешок или сосуд, служащий для подачи вручную к орудиям зарядов из погреба.', 'кокор');
INSERT INTO public.question (id, text, answer) VALUES (2401, 'Воспаление толстой кишки.', 'колит');
