INSERT INTO public.question (id, text, answer) VALUES (2441, 'Продукты растительного и животного происхождения, а также различные минеральные вещества, идущие в пищу животным.', 'корма');
INSERT INTO public.question (id, text, answer) VALUES (2448, 'Самец-тетерев, имеющий хвост с длинными изогнутыми перьями.', 'косач');
INSERT INTO public.question (id, text, answer) VALUES (2459, 'Домашнее животное, ловящее мышей и крыс.', 'кошка');
