INSERT INTO public.question (id, text, answer) VALUES (2473, 'Бугорок земли на низменном или болотистом месте, поросший мхом или травой.', 'кочка');
INSERT INTO public.question (id, text, answer) VALUES (2484, 'Якорь с несколькими лапами.', 'кошка');
INSERT INTO public.question (id, text, answer) VALUES (2489, 'Приз в виде сосуда, вручаемый победителям спортивных соревнований.', 'кубок');
INSERT INTO public.question (id, text, answer) VALUES (2502, 'Дикое непарнокопытное животное рода лошадей.', 'кулан');
INSERT INTO public.question (id, text, answer) VALUES (2506, 'Женское украшение, обычно в виде одного крупного драгоценного камня на цепочке, надеваемое на шею.', 'кулон');
